import Vue from 'vue'
import Vuex from 'vuex'
// 注册插件
Vue.use(Vuex);
// 创建仓库对象
const store = new Vuex.Store({
    // 仓库中的状态（数据）
    state: {
        num: 99,
        str: "hello",
        arr: ["a", "b", "c"]
    },
    // 仓库数据的衍生数据，类似组件的计算结果
    getters: {
        greed(state) {
            return state.str + "world"
        }
    },
    // 仓库数据的   同步  修改方法
    mutations: {
        // 仓库中某数据的mutations命名规则一般为setXxx
        setNum(state, v) {
            state.num = v;
        }
    },
    actions
})
export default store;
